import React, { Component } from 'react';
import ListeMessage from './ListeMessage';
import axios from 'axios';
import Logout from './Logout';
import './Profil.css';
import FormAddMessage from './FormAddMessage';
import ProfilModif from './ProfilModif'

class Profil extends Component{
    constructor(props){
        super(props);
    }

    componentDidMount(){
        // let p = {pseudo}
        // console.log("pseudo:",p.PPpseudo)
        const instance = axios.create({
            baseURL: 'http://localhost:4000/',
            timeout: 1000,
            headers: {'X-Custom-Header': 'foobar'}
        });
        instance.get('api/message/'+this.props.profilpseudo)
        .then(function (response){
            //response.data liste de tous les messages
            console.log("retourne :",response.data)
            //doc = 1message
            setPosts(response.data.map((doc) => doc))
        })
        .catch(function (error){
            console.log("message pas bon")
            alert(error)
            console.log(error)
        })
    }

    render(){
        return(<div>
        <fieldset id = "Profil">
            <div id="enteteP">
                <button type="button" id = "bouttonProfil" onClick={()=>this.props.CallBackAfficheProfil(false)}>Mur de Tweets</button>
                <button type = "button" id = "bouttonProfil">Abonnement</button>
                <button type = "button" id = "bouttonProfil">Abonnes</button>
                <Logout CallBackChangeEtat = {this.props.CallBackChangeEtat}/>
            </div> 
            <div id="Abo">
                <p>liste Abonnes</p>
            </div>
            <div id="mess">
                <FormAddMessage/>
            </div>
            <div id="infoP">
                <ProfilModif/>
            </div>

        </fieldset>
        </div>
        )
    }
}

export default Profil;
