import React, { Component } from 'react';
import NavigationPannel from './NavigationPannel';
import Signin from './Signin';

class MainPage extends Component{
    constructor(props){
        super(props);
        this.state = {pageCourante : "Page de Connexion",connexion : false};
        this.getConnected = this.getConnected.bind(this);
        this.setLogout = this.setLogout.bind(this);
    }

    getConnected(){
        this.setState({pageCourante : "Mur de Tweets",connexion : true});
        return this.state;
    }

    setLogout(){
        this.setState({pageCourante : "Page de Connexion",connexion : false}); 
    }

    render(){
        if(this.state.connexion === false){
            return (<div className = "MainPage">
                <NavigationPannel login={this.getConnected} logout={this.setLogout} 
                isConnected={this.state.connexion} />
            </div>);
        }else{
            return <p>Mur de Tweets</p>
        }
    }
}

export default MainPage;
