class ListeMessage extends Component{
    constructor(props){
        super(props);
        this.state = {ListeM : []};
        this.setState({ListeM : [{message : "Hello"},{message : "How are you"}]})
    }

    render(){
        return(<div className = "ListeMessage">
            {this.state.ListeM.map(Message => (
                <Message Message = {Message}/>)
            )}
        </div>)
    }
}

export default ListeMessage;
