import React, { useState, handleChange, handleSubmit} from 'react';
import ListeMessage from './ListeMessage';
import MurDeTweetsF from './MurDeTweets';
import './FormAddMessage.css';
import axios from 'axios';

function FormAddMessage({passerPseudo}){
    const[message, addMessage] = useState("");

    handleSubmit((event) =>{
        const instance = axios.create({
        baseURL: 'http://localhost:4000/',
        timeout: 5000,
        headers: {'X-Custom-Header': 'foobar'}
        });
        let p = {passerPseudo}
        console.log("form: " ,message)
        instance.put('api/message',{Pseudo : p.passerPseudo, message : message})
        .then(function (response){
            console.log("resultat",response)
            alert("ca marche",response)
        })
        .catch(function (error){
            console.log(error)
            alert("error",error)
        })
    })

    handleChange((event)=>{
        addMessage(document.forms["inscriptionF"].elements["pseudo"].value);
    })

    return(
        // <form className = "FormAddMessage" name = "FormAddMessage">
        //     <div id="message">
        //         <input onChange={m => addMessage(m.target.value)} id = "inputM" value = {message} placeholder="Ecrire votre message" type = "text"></input>
        //         <button className = "button" id = "publierM" onClick={publier}>Publier</button>
        //     </div>
        // </form>
        <form name = "inscriptionF" onSubmit={()=>handleChange()}>
        <fieldset id = "principal">
            <input type = "text" id = "inputI" placeholder="Ecrire votre message" name = "mess" value= {message} onChange= {()=>handleChange()}></input><br/>
            <button type="button" id = "inscription" onClick={()=>handleSubmit()}>Publier</button>
        </fieldset>
        </form>
    )
    }


export default FormAddMessage;
