import './message.css';

const Message = forwardRef(
    ({pseudo, message},ref)=>{

        const voirProfil =(m) =>{
            const instance = axios.create({
            baseURL: 'http://localhost:4000/',
            timeout: 5000,
            headers: {'X-Custom-Header': 'foobar'}
            });
            let p = {passerPseudo}
            console.log("form: " ,message)
            instance.put('api/message',{Pseudo : p.passerPseudo, message : message})
            .then(function (response){
                console.log("resultat",response)
                alert(response)
            })
            .catch(function (error){
                console.log(error)
                alert("error",error)
            })
        }

        return(
            <div className="post" id='Message' ref={ref}>

                <img id="photo"src="papillon.jpg"/>
                <button className = "button" id = "pseudo" onClick={voirProfil}>{pseudo}</button>

                {/* <input id = "date" type = "date" placeholder="Date" name = "date" value= {this.state.date} onChange= {this.handleChange}></input> */}

            <div id="contenu">
                {message}
            </div>
            </div>
        )
    }
)

export default Message;
